-- 处理 大数字 四舍五入
function round(x)
    local f = math.floor(x)
    if x == f then
        return f
    else
        return math.floor(x + 0.5)
    end
end
print("大数字 round: ", round(x))
print("大数字 round: ", round(x + 0.5))

-- 无偏取整，取最近的偶数
function unbiasedRound(x)
    local f = math.floor(x)
    if (x == f) or (x % 2.0 == 0.5) then
        return f
    else
        return math.floor(x + 0.5)
    end
end
print("无偏取整: ", unbiasedRound(2.5))

